import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { addActivity, getAllCountries } from "../../actions";

export const Activities = () => {
  const dispatch = useDispatch();
  useEffect(() => dispatch(getAllCountries()), [dispatch, useState]);
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    dificult: null,
    duration: null,
    season: "",
    countryId: "",
  });
  const handleInputChange = (event) => {
    setInput({ ...input, [event.target.name]: event.target.value });
    // setErrors(
    //   validate({...input, [event.target.name]: event.target.value})
    // )
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(addActivity(input));
    setInput({
      name: "",
      dificult: null,
      duration: null,
      season: "",
      countryId: "",
    });
  };

  const countries = useSelector(state => state.countries)

  const activities = useSelector((state) => state.activities);
  console.log(countries);
  console.log(input);
  console.log(activities);

  return (
    <ActivityForm onSubmit={handleSubmit}>
      <div>
        <label>Nombre:</label>
        <input
          type="text"
          className={errors.name && "danger"}
          name="name"
          onChange={handleInputChange}
          value={input.name}
        />
        {errors.name && <p className="danger">{errors.name}</p>}

        <label>Temporada:</label>
        <input list="season" name="season" onChange={handleInputChange} value={input.season}/>
          <datalist id="season"  >
            <option value="Primavera"></option>
            <option value="Verano"></option>
            <option value="Otoño"></option>
            <option value="Invierno"></option>
          </datalist>

        <label>País:</label>
        <input list="countryId" name="countryId" onChange={handleInputChange} value={input.countryId} />
          <datalist id="countryId" >
            {
              countries?.map(country => 
                <option value={country.countryId}>{country.name}</option>
                )
            }
          </datalist>


        <label>Duración:</label>
        <input type="number" min="1" max="12" size="2"
        name="duration" onChange={handleInputChange}
        value={input.duration}
        />

        <label>Dificultad:</label>
        <input type="number" min="1" max="5"
        name="dificult" onChange={handleInputChange}
        value={input.dificult}
        />

        <button type="submit">Submit</button>
      </div>
    </ActivityForm>
  );
};

const ActivityForm = styled.form`
  div{
    display: flex;
    flex-direction: column;
    height: 50rem;
    width: 50rem;
    margin: 0 auto;

  }
`;
