    }
    section {
      display: flex !important;
      justify-content: space-evenly;
      .icon-contact {
        height: 10rem;
        width: 10rem;
      }
    }
    h1 {
      color: #43597d;
    }
    border: 1px solid black;
    border: 3px solid #fafafa10;
    background: #00000099;
    backdrop-filter: blur(5px);
    color: #fff;
    border-radius: 2rem;
    text-shadow: 4px 1px 8px rgba(0, 0, 0, 0.3);
    box-shadow: 15px 16px 27px -7px rgba(0, 0, 0, 0.69);
    -webkit-box-shadow: 15px 16px 27px -7px rgba(0, 0, 0, 0.69);
    -moz-box-shadow: 15px 16px 27px -7px rgba(0, 0, 0, 0.69);
    text-align: center;
  }

  section {
    display: grid;
    grid-template-rows: 50%;
    #info-page {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      align-items: center;
      text-align: center;
      width: 31rem;
      margin: 1rem auto;
      img {
        height: 20rem;
        width: 20rem;
      }
      h1 {
        color: #43597d;
      }
    }
    #form-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin: auto;

      input {
        margin: 1rem 0;
        width: 40rem;
        font-size: medium;
        line-height: 28px;
        border: 2px solid transparent;
        border-bottom-color: #777;
        padding: 0.2rem 0;
        outline: none;
        background-color: transparent;
        color: #0d0c22;
        transition: 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
        text-align: center;
        &:focus,
        :hover {
          outline: none;
          padding: 0.2rem 1rem;
          border-radius: 1rem;
          border-color: #7a9cc6;
        }
        &::placeholder {
          color: #777;
        }
        &:focus::placeholder {
          opacity: 0;
          transition: opacity 0.5s;
        }
      }
      textarea {
        width: 32rem;
        height: 15rem;
        font-size: medium;
        line-height: 28px;
        border: 2px solid transparent;
        border-bottom-color: #777;
        padding: 0.2rem 0;
        background-color: transparent;
        color: #0d0c22;
        transition: 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
        &:focus,
        :hover {
          padding: 0.2rem 1rem;
          border-radius: 1rem;
          border-color: #7a9cc6;
        }
      }
      h1 {
        color: #43597d;
      }
      label {
        margin: 0.5rem 0;
      }
      button {
        display: flex;
        align-items: center;
        justify-content: center;
        outline: none;
        cursor: pointer;
        width: 7rem;
        height: 3rem;
        font-weight: 700;
        margin: 0.5rem;
        background-image: linear-gradient(
          to top,
          #d8d9db 0%,
          #fff 80%,
          #fdfdfd 100%
        );
        border-radius: 2rem;
        border: 1px solid #8f9092;
        transition: all 0.2s ease;
        font-size: 14px;
        text-shadow: 0 1px #fff;

        &:hover {
          box-shadow: 0 4px 3px 1px #fcfcfc, 0 6px 8px #d6d7d9,
            0 -4px 4px #cecfd1, 0 -6px 4px #fefefe, inset 0 0 3px 3px #cecfd1;
        }

        &:active {
          box-shadow: 0 4px 3px 1px #fcfcfc, 0 6px 8px #d6d7d9,
            0 -4px 4px #cecfd1, 0 -6px 4px #fefefe, inset 0 0 5px 3px #999,
            inset 0 0 1rem #aaa;
        }

        &:focus {
          box-shadow: 0 4px 3px 1px #fcfcfc, 0 6px 8px #d6d7d9,
            0 -4px 4px #cecfd1, 0 -6px 4px #fefefe, inset 0 0 5px 3px #999,
            inset 0 0 1rem #aaa;
        }
      }
    }
  }
`;

export default FormContacto;
