import React from "react";
import emailjs from "@emailjs/browser";
import styled from "styled-components";

const FormContacto = () => {
  const sendEmail = (event) => {
    event.preventDefault();

    emailjs
      .sendForm(
        "service_9qmrtn3", //? service ID
        "template_l0b7d5s", //? template ID
        event.target,
        "KjhABlSMS8yiq-raI" //? API key
      )
      .then((response) => console.log(response))
      .catch((error) => console.log(error));
  };

  return (
    <ContactContainerForm>
      <div>
        <h1>Main</h1>
        
      </div>

      <section>
        <InfoPageContact>
          <h1>Holi</h1>
          <p>
            Esta p치gina es un proyecto hecho con fines educativos. <br />
            <i>QApp-io</i> <b>NO</b> es una empresa real. La creaci칩n del nombre
            como de la im치gen es simulada para mostrar en esta aplicaci칩n web
            los conocimientos adquiridos durante el bootcamp #SoyHenry游눝.
          </p>
        </InfoPageContact>
        <form id="form-container" onSubmit={sendEmail}>
          <h1>Cont치ctame ;)</h1>
          <input type="text" name="user_name" placeholder="Nombre" />
          <hr />

          <input type="email" name="user_email" placeholder="Email" />
          <hr />

          <label>Mensaje</label>
          <textarea name="user_message" id="" cols="30" rows="10"></textarea>
          <hr />
          <button>Enviar</button>
        </form>
      </section>
    </ContactContainerForm>
  );
};

const ContactContainerForm = styled.main`
  width: 130rem;
  height: 86rem;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  padding: 3rem;
  div {
    border: 1px solid black;
    border: 3px solid #fafafa10;
    background: #fafafa10;
    backdrop-filter: blur(5px);
    color: #131516;
    border-radius: 2rem;
    text-shadow: 4px 1px 8px rgba(0, 0, 0, 0.3);
    box-shadow: 15px 16px 27px -7px rgba(0, 0, 0, 0.69);
    -webkit-box-shadow: 15px 16px 27px -7px rgba(0, 0, 0, 0.69);
    -moz-box-shadow: 15px 16px 27px -7px rgba(0, 0, 0, 0.69);
    text-align: center;
  }

  section {
    display: grid;
    grid-template-rows: 50%;
    #form-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin: auto;

      input {
        margin: 1rem 0;
        width: 40rem;
        font-size: medium;
        line-height: 28px;
        border: 2px solid transparent;
        border-bottom-color: #777;
        padding: 0.2rem 0;
        outline: none;
        background-color: transparent;
        color: #0d0c22;
        transition: 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
        text-align: center;
        &:focus,
        :hover {
          outline: none;
          padding: 0.2rem 1rem;
          border-radius: 1rem;
          border-color: #7a9cc6;
        }
        &::placeholder {
          color: #777;
        }
        &:focus::placeholder {
          opacity: 0;
          transition: opacity 0.5s;
        }
      }
      textarea{
        width: 32rem;
        font-size: medium;
        line-height: 28px;
        border: 2px solid transparent;
        border-bottom-color: #777;
        padding: 0.2rem 0;
        background-color: transparent;
        color: #0d0c22;
        transition: 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
        &:focus,
        :hover {
          padding: 0.2rem 1rem;
          border-radius: 1rem;
          border-color: #7a9cc6;
        }
      }
    }
  }
`;

const InfoPageContact = styled.section`
  display: flex;
  flex-direction: column;
  text-align: center;
  width: 31rem;
  margin: 1rem auto;
`;

export default FormContacto;
