import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { addActivity, getAllCountries } from "../../actions";

const ActivitiesForm = () => {
  const dispatch = useDispatch();
  useEffect(() => dispatch(getAllCountries()), [dispatch]);
  // const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    dificult: null,
    duration: null,
    season: "",
    countryIds: [],
  });
  const flags = [];

  const handleInputChange = (event) => {
    setInput({ ...input, [event.target.name]: event.target.value });
    // setErrors(
    //   validate({...input, [event.target.name]: event.target.value})
    // )
  };

  const clickFlags = (event) => {
    event.preventDefault();
    setInput({
      ...input,
      countryIds: input.countryIds.concat(event.target.id),
    });
    // input.countryIds.push(event.target.id);
    flags.push(event.target.id);
    document.getElementById(event.target.id).style.filter = "grayscale(0)";
    document.getElementById(event.target.id).style.scale = "calc(1.2)";
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    flags.map((flag) => {
      document.getElementById(flag).style.filter = "grayscale(100%)";
      return (document.getElementById(flag).style.scale = "none");
    });
    dispatch(addActivity(input));
    setInput({
      name: "",
      dificult: null,
      duration: null,
      season: "",
      countryIds: [],
    });
  };

  const countries = useSelector((state) =>
    state.countries.sort((a, b) => {
      if (a.name > b.name) return 1;
      if (b.name > a.name) return -1;
      return 0;
    })
  );

  const activities = useSelector((state) => state.activities);
  console.log(input);
  console.log(activities);

  return (
    <ActivityForm onSubmit={handleSubmit}>
      <div id="form-container">
        <input
          type="text"
          placeholder="Nombre"
          // className={errors.name && "danger"}
          name="name"
          onChange={handleInputChange}
          value={input.name}
        />
        {/* {errors.name && <p className="danger">{errors.name}</p>} */}

        <input
          list="season"
          name="season"
          placeholder="Temporada"
          onChange={handleInputChange}
          value={input.season}
        />
        <datalist id="season">
          <option value="Primavera"></option>
          <option value="Verano"></option>
          <option value="Otoño"></option>
          <option value="Invierno"></option>
        </datalist>

        <input
          type="number"
          min="1"
          max="12"
          size="2"
          name="duration"
          placeholder="Duración (hrs)"
          onChange={handleInputChange}
          value={input.duration}
        />

        <input
          type="number"
          min="1"
          max="5"
          size="1"
          name="dificult"
          placeholder="Dificultad"
          onChange={handleInputChange}
          value={input.dificult}
        />

        <label>Selecciona el País(es):</label>

        {input.name && input.dificult && input.season && input.duration ? (
          <section>
            {countries?.map((c) => (
              <div key={c.countryId}>
                <Link onClick={clickFlags} to="">
                  <div>
                    <img src={c.flag} alt="flag" id={c.countryId} />
                    <p>{c.countryId}</p>
                  </div>
                </Link>
              </div>
            ))}
          </section>
        ) : (
          <></>
        )}

        <button
          type="submit"
          disabled={
            !input.name ||
            !input.dificult ||
            !input.season ||
            !input.duration ||
            input.countryIds.length < 1
          }
        >
          Crear
        </button>
      </div>
    </ActivityForm>
  );
};

const ActivityForm = styled.form`
  #form-container {
    input {
      line-height: 28px;
      border: 2px solid transparent;
      border-bottom-color: #777;
      padding: 0.2rem 0;
      outline: none;
      background-color: transparent;
      color: #0d0c22;
      transition: 0.3s cubic-bezier(0.645, 0.045, 0.355, 1);
      &:hover,
      :focus {
        outline: none;
        padding: 0.2rem 1rem;
        border-radius: 1rem;
        border-color: #7a9cc6;
      }
      &::placeholder {
        color: #777;
      }
      &::focus::placeholder {
        opacity: 0;
        transition: opacity 0.3s;
      }
    }
  }
  div {
    display: flex;
    flex-direction: column;
    height: 65rem;
    width: 66rem;
    margin: 0 auto;
    section {
      display: grid;
      grid-template-columns: repeat(10, 1fr);
      overflow-y: scroll;
      padding: 1rem 0;
      img {
        border-radius: 0.5rem;
      }

      &::-webkit-scrollbar {
        width: 1rem;
        height: 1rem;
      }
      &::-webkit-scrollbar-track {
        background: #9993a5;
        border-radius: 100vw;
        margin-block: 0.5em;
      }
      &::-webkit-scrollbar-thumb {
        background: #49259c;
        border-radius: 5rem;
        &:hover {
          background: #361c72;
        }
      }

      div {
        display: flex;
        flex-direction: column;
        width: 5rem;
        height: 5rem;
        text-align: center;
        transition: 0.5s;
        scale: none;
        &:hover {
          scale: calc(1.1);
        }
        img {
          filter: grayscale(100%);
          height: 3rem;
          object-fit: cover;
          transition: 0.5s;
          &:hover {
            filter: grayscale(0);
          }
        }
        p {
          margin: 3px 0 0 0;
        }
      }
    }
  }
`;

export default ActivitiesForm;
