import styled from "styled-components";
import { Link } from "react-router-dom";
import avion from "../IMG/avion.png";

export default function HomeMain() {
  return (
    <HomeContainer>
      <Intro>
        <h1>Consulta informaci√≥n relevante sobre tus paises preferidos.</h1>
        <Link to="/countries" className="exp">
          <img src={avion} alt="avion" />
          Explorar
        </Link>
      </Intro>
      <div class="img-container">
        <img
          src="https://i.postimg.cc/kgFy7KSs/goku.png"
          alt="Goku Floating on the flying cloud.Ilustration."
          class="img-goku"
        />
      </div>
    </HomeContainer>
  );
}

const HomeContainer = styled.main`
  display: grid;
  grid-template-columns: 1fr 1fr;
  align-items: center;
  justify-items: center;
  width: 140rem;
  gap: 7rem;
  margin: 20rem auto;
  background-color: #fff;

  h1 {
    color: #43597d;
    font-size: 5rem;
    text-align: left;
  }


  img {
    max-width: 100%;
}

.img-container {
    display: flex;
    justify-content: center;
    align-items: center;
}

.img-container::after {
    content: "";
    width: 250px;
    height: 20px;
    background-color: rgba(0, 0, 0, 0.2);
    position: absolute;
    bottom: 50px;
    border-radius: 50%;
    margin-left: -50px;
    z-index: 30;
    animation: shadow 1s infinite alternate;
}

.img-goku {
    position: absolute;
    z-index: 30;
    bottom: 100px;
    animation: float 1s infinite alternate;
}


@keyframes float {
    to {
        transform: translateY(-30px);
    }
}

@keyframes shadow {
    to {
        transform: scale(0.6);
    }
}

`;

const Intro = styled.section`
  width: 68rem;
  filter: drop-shadow(-1.5rem 0.5rem 1rem #435984);
  img {
    height: 5rem;
  }

  img {
    max-width: 100%;
  }
`;
