import {
  GET_COUNTRIE,
  GET_COUNTRIE_DETAIL,
  ADD_ACTIVITY,
  REMOVE_ACTIVITY,
  GET_ALL_COUNTRIES,
  FILTER_BY_CONTINENT,
  FILTER_BY_ACTIVITY,
  ORDER_BY_NAME,
  ORDER_BY_POPULATION,
} from "../actions";

const initialState = {
  activities: [], //? Acá agrega las actividades
  countries: [], //? carga los paises buscados
  copyCountries: [], //? Apoyo para el filter
  countryDetail: {}, //? Carga toda la data en un obj
};

function rootReducer(state = initialState, action) {
  if (action.type === GET_ALL_COUNTRIES) {
    return {
      ...state,
      countries: action.payload,
      copyCountries: action.payload,
    };
  }
  if (action.type === GET_COUNTRIE) {
    return {
      ...state,
      countries: action.payload,
    };
  }
  if (action.type === REMOVE_ACTIVITY) {
    return {
      ...state,
      activities: state.activities.filter((m) => m.id !== action.payload),
    };
  }
  if (action.type === GET_COUNTRIE_DETAIL) {
    return {
      ...state,
      countryDetail: action.payload,
    };
  }
  if (action.type === ADD_ACTIVITY) {
    return {
      ...state,
      activities: state.activities.concat(action.payload),
    };
  }

  //? <--------- Filters -------->

  if (action.type === FILTER_BY_CONTINENT) {
    const allCountries = state.copyCountries;
    const filteredByContinent =
      action.payload === "All"
        ? allCountries
        : allCountries.filter((c) => c.continent === action.payload);
    return {
      ...state,
      countries: filteredByContinent,
    };
  }

  if (action.type === FILTER_BY_ACTIVITY) {
    const allCountries = state.copyCountries;
    const filteredActivities =
      action.payload === "All"
        ? allCountries
        : allCountries.filter((c) => c.activities.length > 0);
    return {
      ...state,
      countries: filteredActivities,
    };
  }

  //? <--------- Orders -------->

  if (action.type === ORDER_BY_NAME) {
    // let sortedArr =
    //   action.payload === "asc"
    //     ? state.countries.sort(function (a, b) {
    //         if (a.name > b.name) return 1;
    //         if (b.name > a.name) return -1;
    //         return 0;
    //       })
    //     : state.countries.sort(function (a, b) {
    //         if (a.name > b.name) return -1;
    //         if (b.name > b.name) return 1;
    //         return 0;
    //       });
    let sortedArr;
    switch (action.payload) {
      case "asc":
        sortedArr = state.countries.sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name > b.name) return -1;
          return 0;
        });
        return {
      ...state,
      countries: sortedArr,
    };

      case "des":
        sortedArr = state.countries.sort((a, b) => {
          if (a.name > b.name) return -1;
          if (a.name > b.name) return 1;
          return 0;
        });
        return {
      ...state,
      countries: sortedArr,
    };

      case "population":
        sortedArr = state.countries.sort((a, b) => {
          if(a.population > b.population) return -1;
          if(a.population > b.population) return 1;
          return 0;
        });
        return {
          ...state,
          countries: sortedArr,
        }
    }
    
  }

  if (action.type === ORDER_BY_POPULATION) {
    let sortedArr = state.countries.sort(function (a, b) {
      if (a.population > b.population) return -1;
      if (b.population > b.population) return 1;
      return 0;
    });
    return {
      ...state,
      countries: sortedArr,
    };
  }

  return state;
}

export default rootReducer;
