import { useEffect, useState } from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { getAllCountries, getCountry, filterCountriesByContinent, filterByActivities, orderByName } from "../../actions";
import CardPais from "./CardPais";
import footerImg from "../../Img/FooterPagePICountries.png";

export default function Countries(props) {
  const dispatch = useDispatch();
  const [state, setState] = useState({
    country: "",
  });
  const [currentPage, setCurrentPage] = useState(0);
  const [orden, setOrden] = useState("");

  useEffect(() => {
    dispatch(getAllCountries());
  }, [dispatch]);

  const handleChange = (event) => {
    setCurrentPage(0);
    setState({ country: event.target.value });
    dispatch(getCountry(state.country));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(getCountry(state.country));
    setState({ country: "" });
  };

  const countries = useSelector((state) => state.countries);
  console.log(countries);

  const paginado = () => {
    if(state.country.length === 0)
    return countries.slice(currentPage, currentPage + 9);

    return countries.slice(currentPage, currentPage + 9);
  };

  const nextPage = () => {
    if (countries.length > currentPage + 9)
    setCurrentPage(currentPage + 9);
    
  };

  const prevPage = () => {
    if (currentPage > 0) setCurrentPage(currentPage -9)
  }

  const filterByContinent = (event) => {
    setCurrentPage(0);
    dispatch(filterCountriesByContinent(event.target.value));
  };

  const filteredBySeason = (event) => {
    setCurrentPage(0);
    dispatch(filterByActivities(event.target.value));
  };

  const handleOrder = (event) => {
    event.preventDefault();
    setCurrentPage(0);
    dispatch(orderByName(event.target.value));
    setOrden(`Ordenado ${event.target.value}`);
  };

  return (
    <PaginaPrincipal>
      <h1>Encuentra tu país</h1>
      <div>
        <select onChange={handleOrder}>
          <option value="asc">Ascendente</option>
          <option value="des">Descendente</option>
          <option value="population">Población</option>
        </select>

        <select onChange={filteredBySeason}>
          <option value="All">Todas</option>
          <option value="Primavera">Primavera</option>
          <option value="Verano">Verano</option>
          <option value="Otoño">Otoño</option>
          <option value="Invierno">Invierno</option>
        </select>

        <select onChange={filterByContinent}>
          <option value="All">Todos</option>
          <option value="Africa">Africa</option>
          <option value="Antarctica">Antártida</option>
          <option value="Asia">Asia</option>
          <option value="Europe">Europa</option>
          <option value="Oceania">Oceania</option>
          <option value="North America">Norteamérica</option>
          <option value="South America">Sudamérica</option>
        </select>
      </div>
      <CountrySearcher onSubmit={handleSubmit}>
        <label htmlFor="country">País: {""}</label>
        <input
          type="text"
          id="country"
          autoComplete="off"
          value={state.country}
          onChange={handleChange}
        />
        <button type="submit">Buscar</button>
      </CountrySearcher>
      <PaisesContainer>
        {paginado()?.map((country) => (
          <CardPais
            key={country.countryId}
            id={country.countryId}
            flag={country.flag}
            name={country.name}
            continent={country.continent}
          />
        ))}
      </PaisesContainer>
      <PaginadoSection>
        <button className="next" onClick={prevPage}>
          <div className="arrow-wrapper">
            <div className="arrow-back"></div>
          </div>
        </button>
        <button className="next"  onClick={nextPage}>
          <div className="arrow-wrapper">
            <div className="arrow"></div>
          </div>
        </button>
      </PaginadoSection>

      <img src={footerImg} alt="monuments" />
    </PaginaPrincipal>
  );
}

const PaisesContainer = styled.section`
  width: 140rem;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  main {
    border: 5px outset;
    padding: 1rem;
    height: 21rem;
  }
`;

const CountrySearcher = styled.form`

`;


const PaginaPrincipal = styled.main`

`;

const PaginadoSection = styled.div`
  display: flex;
  justify-content: space-around;
  width: 30rem;
  margin: 1rem auto;

    .next {
    box-sizing: border-box;
    border: 0;
    border-radius: 2rem;
    color: #fff;
    padding: 1em 1.8em;
    background: #645bff;
    display: flex;
    transition: 0.4s background;
    align-items: center;
    gap: 0.6em;
    font-weight: bold;

    .arrow-wrapper {
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .arrow,
    .arrow-back {
      margin-top: 1px;
      width: 1rem;
      background: #645bff;
      height: 2px;
      position: relative;
      transition: 0.2s;
    }

    .arrow::before {
      content: "";
      position: absolute;
      border: solid #fff;
      border-width: 0 2px 2px 0;
      display: inline-block;
      top: -3px;
      right: 5px;
      transition: 0.4s;
      padding: 3px;
      transform: rotate(-45deg);
    }

    .arrow-back::before {
      content: "";
      position: absolute;
      border: solid #fff;
      border-width: 0 2px 2px 0;
      display: inline-block;
      top: -3px;
      left: 5px;
      transition: 0.4s;
      padding: 3px;
      transform: rotate(135deg);
    }

    &:hover {
      background-color: #111;
    }

    &:hover .arrow,
    :hover .arrow-back{
      background-color: #fff;
    }

    &:hover .arrow:before {
      right: 0;
    }

    &:hover .arrow-back:before {
      left: 0;
    }
  }
`;
