import React from 'react'
import { Link } from 'react-router-dom'
import styled from 'styled-components'

const CardPais = ({flag, name, continent, id}, props) => {

  return (
    <CardContainer>
      <CardRender >
        <div>
          <Link to={`/country/${id}`}>
            <img src={flag} alt="flag" />
          </Link>
        </div>
        <section>
          <h1>{name}</h1>
          <p>{continent}</p>
        </section>
      </CardRender>
    </CardContainer>
  )
}

export default CardPais



const CardContainer = styled.main`
  
`

const CardRender = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  section {
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    text-align: center;
  }
  div img {
    width: 20rem;
    height: 20rem;
    object-fit: cover;
    transition: all 0.5;
    &:hover{
      transition: parent;
      border-radius: 1rem;
    box-shadow: 16px 17px 24px -6px rgba(0,0,0,0.84);
-webkit-box-shadow: 16px 17px 24px -6px rgba(0,0,0,0.84);
-moz-box-shadow: 16px 17px 24px -6px rgba(0,0,0,0.84);
    }
    
  }

`
