import { getCountry, getCountryDetail } from "../actions";
import { useEffect } from "react";
import { useDispatch, useSelector} from "react-redux";

import React from 'react'
import styled from "styled-components";

export default function CountryDetail(props) {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getCountryDetail(props.match.params.id))
  }, [dispatch])
  
  const country = useSelector(state => state.countryDetail)
  console.log(country);

  return (
    <DetallePais>
      <div>
        <img src={country.flag} alt="flag" />
      </div>
      <h1>Nombre: {country.name}</h1>
      <h2>Capital: {country.capital}</h2>
      <h2>Continente:{country.continent}</h2>
      <p>Subregión: {country.subregion}</p>
      <p>Población: {country.population}</p>
      <p>{country.area}</p>
    </DetallePais>
  )
}


const DetallePais = styled.section`
  display: flex;
  flex-direction: column;
`
