import axios from "axios"
export const GET_COUNTRIE = "GET_COUNTRIE";
export const GET_ALL_COUNTRIES = "GET_ALL_COUNTRIES";
export const GET_COUNTRIE_DETAIL = "GET_COUNTRIE_DETAIL";
