import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";

export const Activities = () => {
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    dificult: null,
    duration: null,
    season: "",
  });
  const handleInputChange = (event) => {
    setInput({ ...input, [event.target.name]: event.target.value });
    // setErrors(
    //   validate({...input, [event.target.name]: event.target.value})
    // )
  };

  const activities = useSelector((state) => state.activities);
  console.log(activities);

  return (
    <ActivityForm>
      <div>
        <label>Nombre:</label>
        <input
          type="text"
          className={errors.name && "danger"}
          name="name"
          onChange={handleInputChange}
          value={input.name}
        />
        {errors.name && <p className="danger">{errors.name}</p>}

        <label>Temporada:</label>
        {/* <input id="season" value={input.season} /> */}
        <select name="season" id="season" >
          <option value={input.season} onChange={handleInputChange}>Primavera</option>
          <option value={input.season} onChange={handleInputChange}>Verano</option>
          <option value={input.season} onChange={handleInputChange}>Oto√±o</option>
          <option value={input.season} onChange={handleInputChange}>Invierno</option>
        </select>
      </div>
    </ActivityForm>
  );
};

const ActivityForm = styled.form`
  div{
    display: flex;
    flex-direction: column;
  }
`;
