import { useEffect, useState } from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { getAllCountries, getCountry } from "../../actions";
import CardPais from "./CardPais";
import footerImg from "../../Img/FooterPagePICountries.png";

export default function Countries(props) {
  const dispatch = useDispatch();
  const [state, setState] = useState({
    country: "",
  })

  useEffect(() => {
    dispatch(getAllCountries());
  }, [dispatch]);

  const handleChange = (event) => {
    setState({ country: event.target.value });
    dispatch(getCountry(state.country));
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(getCountry(state.country));
    setState({ country: ""})
  };


  const countries = useSelector((state) => state.countries);
  console.log(countries);

  const {county} = state;
  return (
    <div>
      <h1>Encuentra tu país</h1>
      <CountrySearcher onSubmit={handleSubmit}>
        <label htmlFor="country">País: {""}</label>
        <input type="text" 
          id="country"
          autoComplete="off"
          value={country}
          onChange={handleChange}
        />
        <button type="submit">Buscar</button>
      </CountrySearcher>
      <PaisesContainer>
        {countries?.map((country) => (
          <CardPais
            key={country.countryId}
            id={country.countryId}
            flag={country.flag}
            name={country.name}
            continent={country.continent}
          />
        ))}
      </PaisesContainer>
      <img src={footerImg} alt="monuments" />
    </div>
  );
}

const PaisesContainer = styled.section`
  width: 120rem;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  img {
    width: 100%;
  }
  main {
    border: 5px outset;
    padding: 1rem;
  }
`;

const CountrySearcher = styled.form`

`;
