import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { addActivity, getAllCountries } from "../../actions";


const ActivitiesForm = () => {
  const dispatch = useDispatch();
  useEffect(() => dispatch(getAllCountries()), [dispatch]);
  // const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    dificult: null,
    duration: null,
    season: "",
    countryIds: [],
  });
  const flags = [];

  const handleInputChange = (event) => {
    setInput({ ...input, [event.target.name]: event.target.value });
    // setErrors(
    //   validate({...input, [event.target.name]: event.target.value})
    // )
  };

  const clickFlags = (event) => {
    event.preventDefault();
    console.log(event.target.id);
    input.countryIds.push(event.target.id);
    flags.push(event.target.id);
    document.getElementById(event.target.id).style.filter = "grayscale(0)";
    document.getElementById(event.target.id).style.scale = "calc(1.2)";
    console.log(flags);
  };



  const handleSubmit = (event) => {
    event.preventDefault();
    flags.map((flag) => {
      document.getElementById(flag).style.filter = "grayscale(100%)";
      document.getElementById(flag).style.scale = "none";
    });
    dispatch(addActivity(input));
    setInput({
      name: "",
      dificult: null,
      duration: null,
      season: "",
      countryIds: [],
    });
    // document.getElementById("form-container").append(NavBar)
  };

  const countries = useSelector((state) => state.countries);

  const activities = useSelector((state) => state.activities);
  console.log(input);
  console.log(activities);

  return (
    <ActivityForm onSubmit={handleSubmit}>
      <div id="form-container">
        <label>Nombre:</label>
        <input
          type="text"
          // className={errors.name && "danger"}
          name="name"
          onChange={handleInputChange}
          value={input.name}
        />
        {/* {errors.name && <p className="danger">{errors.name}</p>} */}

        <label>Temporada:</label>
        <input
          list="season"
          name="season"
          onChange={handleInputChange}
          value={input.season}
        />
        <datalist id="season">
          <option value="Primavera"></option>
          <option value="Verano"></option>
          <option value="Otoño"></option>
          <option value="Invierno"></option>
        </datalist>

        <label>Duración:</label>
        <input
          type="number"
          min="1"
          max="12"
          size="2"
          name="duration"
          onChange={handleInputChange}
          value={input.duration}
        />

        <label>Dificultad:</label>
        <input
          type="number"
          min="1"
          max="5"
          name="dificult"
          onChange={handleInputChange}
          value={input.dificult}
        />

        <label>Selecciona el País(es):</label>
        <section>
          {countries?.map((c) => (
            <div key={c.countryId}>
              <Link onClick={clickFlags} to="">
                <div>
                  <img src={c.flag} alt="flag" id={c.countryId} />
                  <p>{c.countryId}</p>
                </div>
              </Link>
            </div>
          ))}
        </section>

        <button
          type="submit"
          disabled={
            !input.name || !input.dificult || !input.season || !input.duration
          }
        >
          Crear
        </button>
      </div>
    </ActivityForm>
  );
};

const ActivityForm = styled.form`
  div {
    display: flex;
    flex-direction: column;
    height: 53rem;
    width: 60rem;
    margin: 0 auto;
    section {
      display: grid;
      grid-template-columns: repeat(10, 1fr);
      gap: 5px;
      overflow-y: scroll;
      background-image: linear-gradient(45deg, #926b18f0, transparent);
      &::-webkit-scrollbar {
        width: 2em;
        height: 1rem;
      }

      div {
        display: flex;
        flex-direction: column;
        width: 5rem;
        height: 5rem;
        text-align: center;
        transition: 0.5s;
        scale: none;
        &:hover {
          scale: calc(1.1);
        }
        img {
          filter: grayscale(100%);
          height: 3rem;
          object-fit: cover;
          transition: 0.5s;
          &:hover {
            filter: grayscale(0);
          }
        }
        p {
          margin: 3px 0 0 0;
        }
      }
    }
  }
`;

export default ActivitiesForm;
