import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { addActivity, getAllCountries } from "../../actions";

const ActivitiesForm = () => {
  const dispatch = useDispatch();
  useEffect(() => dispatch(getAllCountries()), [dispatch]);
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    dificult: 0,
    duration: 0,
    season: "",
    countryIds: [],
  });
  const handleInputChange = (event) => {
    setInput({ ...input, [event.target.name]: event.target.value });
    // setErrors(
    //   validate({...input, [event.target.name]: event.target.value})
    // )
  };

  const clickFlags = (event) => {
    event.preventDefault();
    console.log(event.target.id);
    input.countryIds.push(event.target.id);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(addActivity(input));
    setInput({
      name: "",
      dificult: 0,
      duration: 0,
      season: "",
      countryIds: [],
    });
  };

  const countries = useSelector((state) => state.countries);

  const activities = useSelector((state) => state.activities);
  console.log(countries);
  console.log(input);
  console.log(activities);

  return (
    <ActivityForm onSubmit={handleSubmit}>
      <div>
        <label>Nombre:</label>
        <input
          type="text"
          className={errors.name && "danger"}
          name="name"
          onChange={handleInputChange}
          value={input.name}
        />
        {errors.name && <p className="danger">{errors.name}</p>}

        <label>Temporada:</label>
        <input
          list="season"
          name="season"
          onChange={handleInputChange}
          value={input.season}
        />
        <datalist id="season">
          <option value="Primavera"></option>
          <option value="Verano"></option>
          <option value="Otoño"></option>
          <option value="Invierno"></option>
        </datalist>


        
        {/* <input
          list="countryIds"
          name="countryIds"
          multiple
          onChange={handleInputChange}
          value={input.countryIds}
        />
        <datalist id="countryIds" autocomplete="off">
          {countries?.map((country) => (
            <option value={country.countryId}>{country.name}</option>
          ))}
        </datalist> */}

        <label>Duración:</label>
        <input
          type="number"
          min="1"
          max="12"
          size="2"
          name="duration"
          onChange={handleInputChange}
          value={input.duration}
        />

        <label>Dificultad:</label>
        <input
          type="number"
          min="1"
          max="5"
          name="dificult"
          onChange={handleInputChange}
          value={input.dificult}
        />


        <label>Selecciona el País(es):</label>
        <section>
          {countries?.map((c) => (
            // <main>
            <div key={c.countryId}>
              <Link onClick={clickFlags} to="">
                <div>
                  <img src={c.flag} alt="flag" id={c.countryId} />
                  <p>{c.countryId}</p>
                </div>
              </Link>
            </div>
          ))}
        </section>


        <button
          type="submit"
          disabled={
            !input.name || !input.dificult || !input.season || !input.duration
          }
        >
          Crear
        </button>
      </div>
    </ActivityForm>
  );
};

const ActivityForm = styled.form`
  div {
    display: flex;
    flex-direction: column;
    height: 50rem;
    width: 50rem;
    margin: 0 auto;
    section {
      display: grid;
      grid-template-columns: repeat(10, 1fr);
      gap: 5px;
      div {
        display: flex;
        flex-direction: column;
        width: 5rem;
        height: 5rem;
        text-align: center;
        img {
          height: 3rem;
          object-fit: cover;
        }
        p {
          margin: 0;
        }
      }
    }
  }
`;

export default ActivitiesForm;
