import { getCountryDetail } from "../actions";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import React from "react";
import styled from "styled-components";
import CardActivities from "./CardActivities";

export default function CountryDetail(props) {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getCountryDetail(props.match.params.id));
  }, [dispatch]);


  const country = useSelector((state) => state.countryDetail);
  console.log(country);

  return (
    <PageSectionCountryDetail>
      <DetallePais>
        <div>
          <img src={country.flag} alt="flag" />
        </div>
        <h1>Nombre: {country.name}</h1>
        <h2>Capital: {country.capital}</h2>
        <h2>Continente: {country.continent}</h2>
        <p>Subregión: {country.subregion}</p>
        <p>Población: {country.population} habitantes</p>
        <p>
          Área: {country.area} km<sup>2</sup>
        </p>
        <p>{country.maps}</p>
      </DetallePais>

      <RelacionActivities>
        {
          country.activities?.map(act => 
            <CardActivities 
            id={act.activityId}
            name={act.name}
            season={act.season}
            duration={act.duration}
            dificult={act.dificult}
            created={act.Country_Activity.createdAt.slice(0, 7)}
            />
            )
        }
      </RelacionActivities>
    </PageSectionCountryDetail>
  );
}

const PageSectionCountryDetail = styled.main`
  display: grid;
  grid-template-columns: 2fr 3fr;
  width: 120rem;
  margin: 2rem auto;
  gap: 4rem;
`




const DetallePais = styled.section`
  display: flex;
  flex-direction: column;
  text-align: center;
  border: 1px solid;
  border-radius: 1rem;
  div img {
    width: 20rem;
    height: 20rem;
    object-fit: cover;
    border-radius: 1rem;
    box-shadow: 16px 17px 24px -6px rgba(0, 0, 0, 0.84);
    -webkit-box-shadow: 16px 17px 24px -6px rgba(0, 0, 0, 0.84);
    -moz-box-shadow: 16px 17px 24px -6px rgba(0, 0, 0, 0.84);
  }
`;

const RelacionActivities = styled.section`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: repeat(4, 1fr);
  gap: 2rem;
  
`;
