import styled from "styled-components";
import { Link } from "react-router-dom";
import logo from "./HOME/LogoPIprueba.png";
// import homeIcon from "../Img/HomeIcon.png";

export default function Nav() {
  return (
    <NavBarStyle>
      <img src={logo} alt="logo" />
      <section>
        <Link to="/">
          {/* <img src={homeIcon} alt="home" /> */}
          <p>Home</p>
        </Link>
        <Link to="/countries" className="linknav">
          <p>Ver paises</p>
        </Link>
        <Link to="/activities" className="linknav">
          <p>Actividades</p>
        </Link>
        <Link to="/contact" className="linknav">
          <p>Contacto</p>
        </Link>
      </section>
    </NavBarStyle>
  );
}

const NavBarStyle = styled.div`
  display: grid;
  grid-template-columns: 15rem 5fr;
  align-items: center;
  justify-items: center;
  padding: 1rem 0;
  transition: all .8s;
  font-weight: bold;
  section{
    display: flex;
    width: 60%;
    justify-content: space-around;
    align-items: center;
    font-size: 2rem;
  }
  img {
    height: 6rem;
  }
  section img{
    height: 3rem;
  }
  .linknav{
  /* transition: inherit; */
  position: relative;
  /* box-shadow: inset 0 0 0 0 #037bc0;
  inset: 0; */
  transition: all .8s ease-in-out;
}

.linknav::before{
  content: "";
  height: .1rem;
  width: 100%;
  position: absolute;
  left: 0;
  top: 100%;
  background-color: #037bc0;
  opacity: 0;
  transition: all 1.5s;
}

.linknav:hover{
  font-size: x-large;
  color:#037bc0;

  
}


.linknav:hover::before{
  transition: inherit;
  transform: scale(1.5);
  opacity: 1;
}
`;
