import { 
  GET_COUNTRIE,
  GET_COUNTRIE_DETAIL,
  ADD_ACTIVITY,
  REMOVE_ACTIVITY
} from "../actions";


const initialState = {
  // moviesFavourites: [], //? Acá agrega las pelis favv
  countriesLoaded: [],   //? carga las pelis buscadas
  // movieDetail: {},    //? Carga toda la data en un obj 
};

function rootReducer(state = initialState, action) {

  if (action.type === GET_COUNTRIE) {
      return {
        ...state,
        countriesLoaded: action.payload.Search
      };
  }
  if(action.type === "REMOVE_MOVIE_FAVORITE"){
    return {
      ...state,
      moviesFavourites: state.moviesFavourites.filter(m => m.id !== action.payload)
    }
  }
  if (action.type === "GET_MOVIE_DETAIL"){
    return{
      ...state,
      movieDetail: action.payload
    }
  }

  return state;
}


export default rootReducer;
