import { useEffect, useState } from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { getAllCountries, getCountry } from "../../actions";
import CardPais from "./CardPais";
import footerImg from "../../Img/FooterPagePICountries.png";

export default function Countries(props) {
  const dispatch = useDispatch();
  const [state, setState] = useState({
    country: "",
  })

  useEffect(() => {
    dispatch(getAllCountries());
  }, [dispatch]);


  const handleChange = (event) => {
    setState({ country: event.target.value });
    dispatch(getCountry(state.country));
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(getCountry(state.country));
    setState({ country: ""});
  };


  const countries = useSelector((state) => state.countries);
  console.log(countries);

    const paginado = () => {
    return countries.slice(0, 9)
  };

  return (
    <mainPage>
      <h1>Encuentra tu país</h1>
      <CountrySearcher onSubmit={handleSubmit}>
        <label htmlFor="country">País: {""}</label>
        <input type="text" 
          id="country"
          autoComplete="off"
          value={state.country}
          onChange={handleChange}
        />
        <button type="submit">Buscar</button>
      </CountrySearcher>
      <PaisesContainer>
        {paginado()?.map((country) => (
          <CardPais
            key={country.countryId}
            id={country.countryId}
            flag={country.flag}
            name={country.name}
            continent={country.continent}
          />
        ))}
      </PaisesContainer>
      <button className="next">Siguiente</button>
      <img src={footerImg} alt="monuments" />
    </mainPage>
  );
}

const PaisesContainer = styled.section`
  width: 120rem;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  img {
    width: 100%;
  }
  main {
    border: 5px outset;
    padding: 1rem;
  }
`;

const CountrySearcher = styled.form`

`;

const mainPage = styled.main`
  button {
  background: #ce1d1d;
  padding: 10px 50px 10px 30px;
  color: white;
  font-family: Verdana, Geneva, Tahoma, sans-serif;
  font-size: 20px;
  border-radius: 80px;
  text-transform: uppercase;
  transition: all .5s ease-in-out;
  border: none;
}

button:hover:before {
  width: 79px;
  height: 79px;
  transform: translate(4.6pc, -1.8pc);
  background-image: url("data:image/svg+xml;base64,");
}

button::before {
  content: "";
  background-image: url("data:image/svg+xml;base64,");
  height: 20px;
  width: 20px;
  position: absolute;
  transition: all .5s ease-in-out;
  background-size: 100%;
  transform: translate(6.3pc, 0.2pc);
}
`
