import { useEffect, useState } from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { getAllCountries, getCountry } from "../../actions";
import CardPais from "./CardPais";
import footerImg from "../../Img/FooterPagePICountries.png";

export default function Countries(props) {
  const dispatch = useDispatch();
  const [state, setState] = useState({
    country: "",
  });

  useEffect(() => {
    dispatch(getAllCountries());
  }, [dispatch]);

  const handleChange = (event) => {
    setState({ country: event.target.value });
    dispatch(getCountry(state.country));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(getCountry(state.country));
    setState({ country: "" });
  };

  const countries = useSelector((state) => state.countries);
  console.log(countries);

  const paginado = () => {
    return countries.slice(0, 9);
  };

  return (
    <PaginaPrincipal>
      <h1>Encuentra tu país</h1>
      <CountrySearcher onSubmit={handleSubmit}>
        <label htmlFor="country">País: {""}</label>
        <input
          type="text"
          id="country"
          autoComplete="off"
          value={state.country}
          onChange={handleChange}
        />
        <button type="submit">Buscar</button>
      </CountrySearcher>
      <PaisesContainer>
        {paginado()?.map((country) => (
          <CardPais
            key={country.countryId}
            id={country.countryId}
            flag={country.flag}
            name={country.name}
            continent={country.continent}
          />
        ))}
      </PaisesContainer>
      <PaginadoSection>
        <button className="next">
          <div class="arrow-wrapper">
            <div class="arrow-back"></div>
          </div>
        </button>
        <button className="next">
          <div class="arrow-wrapper">
            <div class="arrow"></div>
          </div>
        </button>
      </PaginadoSection>

      <img src={footerImg} alt="monuments" />
    </PaginaPrincipal>
  );
}

const PaisesContainer = styled.section`
  width: 120rem;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  main {
    border: 5px outset;
    padding: 1rem;
  }
`;

const CountrySearcher = styled.form``;


const PaginaPrincipal = styled.main`

`;

const PaginadoSection = styled.div`
  display: flex;
  justify-content: space-around;
  width: 30rem;
  margin: 1rem auto;

    .next {
    box-sizing: border-box;
    border: 0;
    border-radius: 2rem;
    color: #fff;
    padding: 1em 1.8em;
    background: #645bff;
    display: flex;
    transition: 0.4s background;
    align-items: center;
    gap: 0.6em;
    font-weight: bold;

    .arrow-wrapper {
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .arrow,
    .arrow-back {
      margin-top: 1px;
      width: 1rem;
      background: #645bff;
      height: 2px;
      position: relative;
      transition: 0.2s;
    }

    .arrow::before {
      content: "";
      position: absolute;
      border: solid #fff;
      border-width: 0 2px 2px 0;
      display: inline-block;
      top: -3px;
      right: 3px;
      transition: 0.4s;
      padding: 3px;
      transform: rotate(-45deg);
    }

    .arrow-back::after {
      content: "";
      position: absolute;
      border: solid #fff;
      border-width: 0 -2px -2px 0;
      display: inline-block;
      top: -3px;
      right: 3px;
      transition: 0.4s;
      padding: 3px;
      transform: rotate(135deg);
    }

    &:hover {
      background-color: #111;
    }

    &:hover .arrow,
    :hover .arrow-back{
      background-color: #fff;
    }

    &:hover .arrow:before {
      right: 0;
    }

    &:hover .arrow-back:before {
      left: 0;
    }
  }
`;
