import { getCountry, getCountryDetail } from "../actions";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import React from "react";
import styled from "styled-components";

const InfoCountry = (props) => {
  const dispatch = useDispatch();
  useEffect(() => {
    console.log(props);
    dispatch(getCountryDetail(props.match.params.id));
  }, [dispatch]);

  const country = useSelector((state) => state.countryDetail);
  console.log(country);

  return (
    <DetallePais>
      <div>
        <img src={country.flag} alt="flag" />
      </div>
      <h1>Nombre: {country.name}</h1>
      <h2>Capital: {country.capital}</h2>
      <h2>Continente: {country.continent}</h2>
      <p>Subregión: {country.subregion}</p>
      <p>Población: {country.population} habitantes</p>
      <p>Área: {country.area} km<sup>2</sup></p>
      <p>{country.maps}</p>
    </DetallePais>
  );
}

const DetallePais = styled.section`
  display: flex;
  flex-direction: column;
  text-align: center;
  div img {
    width: 20rem;
    height: 20rem;
    object-fit: cover;
    border-radius: 1rem;
    box-shadow: 16px 17px 24px -6px rgba(0, 0, 0, 0.84);
    -webkit-box-shadow: 16px 17px 24px -6px rgba(0, 0, 0, 0.84);
    -moz-box-shadow: 16px 17px 24px -6px rgba(0, 0, 0, 0.84);
  }
`;

export default InfoCountry;
